	function fuzz(x,y,w,h){
		ctx.fillStyle = 'white'
		for(var i=x; i < x+w; i+= 2){
			for(var j = y; j < y + h; j+=2){
				ctx.globalAlpha = Math.random();
				if(Math.random() < 0.3) ctx.fillRect(i,j,2,2);
			}
		}
		ctx.globalAlpha = 1;
	}
	
	function fuzz2(x,y,w,h){
		ctx.fillStyle = 'white'
		for(var i=x; i < x+w; i+= 2){
			for(var j = y; j < y + h; j+=2){
				ctx.globalAlpha = Math.random();
				if(Math.random() < 0.05) ctx.fillRect(i,j,2,2);
			}
		}
		ctx.globalAlpha = 1;
	}
	
	
