	function dist( p1x, p1y, p2x, p2y )
    {
        var xs = 0;
        var ys = 0;
 
        xs = p2x - p1x;
        xs = xs * xs;
 
        ys = p2y - p1y;
        ys = ys * ys;
 
        return Math.sqrt( xs + ys );
    }

